import React, { useState } from 'react';

function ProfilePage() {
  const [isEditing, setIsEditing] = useState(false);
  const [profileData, setProfileData] = useState({
    // Personal Details
    name: 'John Doe',
    email: 'john.doe@example.com',
    phone: '+91 9876543210',
    department: 'Computer Science',
    batch: '2020',
    rollNumber: 'CS20001',
    dateOfBirth: '1998-05-15',
    
    // Professional Details
    jobTitle: 'Software Engineer',
    company: 'Tech Corp',
    location: 'Bangalore, India',
    experience: '3 years',
    skills: 'React, Node.js, Python',
    linkedinUrl: 'https://linkedin.com/in/johndoe',
    currentSalary: '12 LPA'
  });

  const [formData, setFormData] = useState({ ...profileData });
  const [verificationStatus, setVerificationStatus] = useState('Not Verified');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleUpdateSubmit = (e) => {
    e.preventDefault();
    setProfileData({ ...formData });
    setIsEditing(false);
    alert('Profile updated successfully!');
  };

  const handleRequestVerification = () => {
    setVerificationStatus('Verification Requested');
    alert('Verification request submitted successfully! You will be notified once your profile is verified by the admin.');
  };

  const handleCancelEdit = () => {
    setFormData({ ...profileData });
    setIsEditing(false);
  };

  const inputStyle = {
    width: '100%',
    padding: '8px',
    margin: '4px 0',
    border: '1px solid #ccc',
    borderRadius: '4px',
    fontSize: '14px'
  };

  const buttonStyle = {
    padding: '10px 20px',
    margin: '8px 4px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '14px'
  };

  const primaryButtonStyle = {
    ...buttonStyle,
    backgroundColor: '#007bff',
    color: 'white'
  };

  const secondaryButtonStyle = {
    ...buttonStyle,
    backgroundColor: '#6c757d',
    color: 'white'
  };

  const successButtonStyle = {
    ...buttonStyle,
    backgroundColor: '#28a745',
    color: 'white'
  };

  return (
    <div style={{ padding: '2rem', maxWidth: '800px', margin: '0 auto' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
        <h2>Profile Page</h2>
        <div>
          <span style={{ 
            padding: '4px 12px', 
            borderRadius: '20px', 
            fontSize: '12px',
            backgroundColor: verificationStatus === 'Verified' ? '#d4edda' : 
                           verificationStatus === 'Verification Requested' ? '#fff3cd' : '#f8d7da',
            color: verificationStatus === 'Verified' ? '#155724' : 
                   verificationStatus === 'Verification Requested' ? '#856404' : '#721c24',
            border: `1px solid ${verificationStatus === 'Verified' ? '#c3e6cb' : 
                                verificationStatus === 'Verification Requested' ? '#ffeaa7' : '#f5c6cb'}`
          }}>
            {verificationStatus}
          </span>
        </div>
      </div>

      {!isEditing ? (
        // Display Mode
        <div>
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '2rem', marginBottom: '2rem' }}>
            {/* Personal Details */}
            <div style={{ border: '1px solid #e0e0e0', borderRadius: '8px', padding: '1.5rem' }}>
              <h3 style={{ marginTop: 0, color: '#333', borderBottom: '2px solid #007bff', paddingBottom: '8px' }}>
                Personal Details
              </h3>
              <div style={{ lineHeight: '1.8' }}>
                <p><strong>Name:</strong> {profileData.name}</p>
                <p><strong>Email:</strong> {profileData.email}</p>
                <p><strong>Phone:</strong> {profileData.phone}</p>
                <p><strong>Department:</strong> {profileData.department}</p>
                <p><strong>Batch:</strong> {profileData.batch}</p>
                <p><strong>Roll Number:</strong> {profileData.rollNumber}</p>
                <p><strong>Date of Birth:</strong> {new Date(profileData.dateOfBirth).toLocaleDateString()}</p>
              </div>
            </div>

            {/* Professional Details */}
            <div style={{ border: '1px solid #e0e0e0', borderRadius: '8px', padding: '1.5rem' }}>
              <h3 style={{ marginTop: 0, color: '#333', borderBottom: '2px solid #28a745', paddingBottom: '8px' }}>
                Professional Details
              </h3>
              <div style={{ lineHeight: '1.8' }}>
                <p><strong>Job Title:</strong> {profileData.jobTitle}</p>
                <p><strong>Company:</strong> {profileData.company}</p>
                <p><strong>Location:</strong> {profileData.location}</p>
                <p><strong>Experience:</strong> {profileData.experience}</p>
                <p><strong>Skills:</strong> {profileData.skills}</p>
                <p><strong>LinkedIn:</strong> 
                  <a href={profileData.linkedinUrl} target="_blank" rel="noopener noreferrer" style={{ color: '#007bff', marginLeft: '8px' }}>
                    View Profile
                  </a>
                </p>
                <p><strong>Current Salary:</strong> {profileData.currentSalary}</p>
              </div>
            </div>
          </div>

          <div style={{ textAlign: 'center' }}>
            <button 
              onClick={() => setIsEditing(true)}
              style={primaryButtonStyle}
            >
              Update Details
            </button>
            <button 
              onClick={handleRequestVerification}
              style={successButtonStyle}
              disabled={verificationStatus === 'Verification Requested'}
            >
              {verificationStatus === 'Verification Requested' ? 'Verification Requested' : 'Request Verification'}
            </button>
          </div>
        </div>
      ) : (
        // Edit Mode
        <form onSubmit={handleUpdateSubmit}>
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '2rem', marginBottom: '2rem' }}>
            {/* Personal Details Form */}
            <div style={{ border: '1px solid #e0e0e0', borderRadius: '8px', padding: '1.5rem' }}>
              <h3 style={{ marginTop: 0, color: '#333', borderBottom: '2px solid #007bff', paddingBottom: '8px' }}>
                Update Personal Details
              </h3>
              <div>
                <label>Name:</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  style={inputStyle}
                  required
                />
                
                <label>Email:</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  style={inputStyle}
                  required
                />
                
                <label>Phone:</label>
                <input
                  type="tel"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                  style={inputStyle}
                  required
                />
                
                <label>Department:</label>
                <select
                  name="department"
                  value={formData.department}
                  onChange={handleInputChange}
                  style={inputStyle}
                  required
                >
                  <option value="Computer Science">Computer Science</option>
                  <option value="Electronics">Electronics</option>
                  <option value="Mechanical">Mechanical</option>
                  <option value="Civil">Civil</option>
                  <option value="Chemical">Chemical</option>
                </select>
                
                <label>Batch:</label>
                <input
                  type="text"
                  name="batch"
                  value={formData.batch}
                  onChange={handleInputChange}
                  style={inputStyle}
                  required
                />
                
                <label>Roll Number:</label>
                <input
                  type="text"
                  name="rollNumber"
                  value={formData.rollNumber}
                  onChange={handleInputChange}
                  style={inputStyle}
                  required
                />
                
                <label>Date of Birth:</label>
                <input
                  type="date"
                  name="dateOfBirth"
                  value={formData.dateOfBirth}
                  onChange={handleInputChange}
                  style={inputStyle}
                  required
                />
              </div>
            </div>

            {/* Professional Details Form */}
            <div style={{ border: '1px solid #e0e0e0', borderRadius: '8px', padding: '1.5rem' }}>
              <h3 style={{ marginTop: 0, color: '#333', borderBottom: '2px solid #28a745', paddingBottom: '8px' }}>
                Update Professional Details
              </h3>
              <div>
                <label>Job Title:</label>
                <input
                  type="text"
                  name="jobTitle"
                  value={formData.jobTitle}
                  onChange={handleInputChange}
                  style={inputStyle}
                  required
                />
                
                <label>Company:</label>
                <input
                  type="text"
                  name="company"
                  value={formData.company}
                  onChange={handleInputChange}
                  style={inputStyle}
                  required
                />
                
                <label>Location:</label>
                <input
                  type="text"
                  name="location"
                  value={formData.location}
                  onChange={handleInputChange}
                  style={inputStyle}
                  required
                />
                
                <label>Experience:</label>
                <input
                  type="text"
                  name="experience"
                  value={formData.experience}
                  onChange={handleInputChange}
                  style={inputStyle}
                  placeholder="e.g., 3 years"
                  required
                />
                
                <label>Skills:</label>
                <textarea
                  name="skills"
                  value={formData.skills}
                  onChange={handleInputChange}
                  style={{ ...inputStyle, height: '60px', resize: 'vertical' }}
                  placeholder="e.g., React, Node.js, Python"
                  required
                />
                
                <label>LinkedIn URL:</label>
                <input
                  type="url"
                  name="linkedinUrl"
                  value={formData.linkedinUrl}
                  onChange={handleInputChange}
                  style={inputStyle}
                  placeholder="https://linkedin.com/in/yourprofile"
                />
                
                <label>Current Salary:</label>
                <input
                  type="text"
                  name="currentSalary"
                  value={formData.currentSalary}
                  onChange={handleInputChange}
                  style={inputStyle}
                  placeholder="e.g., 12 LPA"
                />
              </div>
            </div>
          </div>

          <div style={{ textAlign: 'center' }}>
            <button 
              type="submit"
              style={primaryButtonStyle}
            >
              Save Changes
            </button>
            <button 
              type="button"
              onClick={handleCancelEdit}
              style={secondaryButtonStyle}
            >
              Cancel
            </button>
          </div>
        </form>
      )}
    </div>
  );
}

export default ProfilePage;
