import React from 'react';

const testimonials = [
  {
    message: "My years at the campus were unforgettable. The faculty and friends made it special.",
    name: "Rahul Sharma",
    year: 2018
  },
  {
    message: "The exposure and opportunities I got here shaped my career.",
    name: "Priya Singh",
    year: 2019
  },
  {
    message: "A beautiful campus and a vibrant student community!",
    name: "Amit Patel",
    year: 2020
  },
  {
    message: "The best years of my life. Proud to be an alumnus.",
    name: "Sneha Rao",
    year: 2017
  }
];

function Testimonials() {
  return (
    <div style={{ maxWidth: 800, margin: '2rem auto' }}>
      <h2>Alumni Testimonials</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: 16 }}>
        {testimonials.map((t, i) => (
          <div key={i} style={{
            flex: '1 1 220px',
            background: '#f9f9f9',
            borderRadius: 8,
            padding: 16,
            boxShadow: '0 2px 8px rgba(0,0,0,0.07)'
          }}>
            <p style={{ fontStyle: 'italic' }}>"{t.message}"</p>
            <p><b>{t.name}</b> <span style={{ color: '#888' }}>({t.year})</span></p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Testimonials;
