import React, { useState, useEffect } from 'react';

const images = [
  'https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=800&q=80',
  'https://images.unsplash.com/photo-1465101046530-73398c7f28ca?auto=format&fit=crop&w=800&q=80',
  'https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=crop&w=800&q=80',
  'https://images.unsplash.com/photo-1503676382389-4809596d5290?auto=format&fit=crop&w=800&q=80',
  'https://images.unsplash.com/photo-1519125323398-675f0ddb6308?auto=format&fit=crop&w=800&q=80'
];

function ImageSlider() {
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const timer = setTimeout(() => setIndex((index + 1) % images.length), 3000);
    return () => clearTimeout(timer);
  }, [index]);

  return (
    <div style={{ maxWidth: 800, margin: '2rem auto', position: 'relative' }}>
      <img
        src={images[index]}
        alt="Campus"
        style={{ width: '100%', height: 350, objectFit: 'cover', borderRadius: 12 }}
      />
      <div style={{ position: 'absolute', bottom: 10, left: '50%', transform: 'translateX(-50%)' }}>
        {images.map((_, i) => (
          <span
            key={i}
            style={{
              display: 'inline-block',
              width: 10,
              height: 10,
              borderRadius: '50%',
              background: i === index ? '#333' : '#ccc',
              margin: '0 4px'
            }}
            onClick={() => setIndex(i)}
          />
        ))}
      </div>
    </div>
  );
}

export default ImageSlider;
