import React, { useState } from 'react';

function ContactForm() {
  const [form, setForm] = useState({ name: '', email: '', message: '' });
  const [status, setStatus] = useState('');

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setStatus('Sending...');
    try {
      const res = await fetch('/api/contact', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
      });
      const data = await res.json();
      if (res.ok) {
        setStatus('Message sent!');
        setForm({ name: '', email: '', message: '' });
      } else {
        setStatus(data.error || 'Failed to send.');
      }
    } catch {
      setStatus('Failed to send.');
    }
  };

  const getStatusClass = () => {
    if (status === 'Message sent!') return 'status-message status-success';
    if (status === 'Sending...') return 'status-message status-sending';
    if (status && status !== 'Message sent!') return 'status-message status-error';
    return '';
  };

  return (
    <div className="contact-container">
      <div className="contact-form">
        <h2>Contact Us</h2>
        <input
          name="name"
          placeholder="Your Name"
          value={form.name}
          onChange={handleChange}
          required
          className="form-input"
        />
        <input
          name="email"
          type="email"
          placeholder="Your Email"
          value={form.email}
          onChange={handleChange}
          required
          className="form-input"
        />
        <textarea
          name="message"
          placeholder="Your Message"
          value={form.message}
          onChange={handleChange}
          required
          className="form-textarea"
        />
        <button 
          type="submit" 
          onClick={handleSubmit}
          disabled={status === 'Sending...'}
          className="submit-btn"
        >
          {status === 'Sending...' ? 'Sending...' : 'Send Message'}
        </button>
        {status && <div className={getStatusClass()}>{status}</div>}
      </div>
    </div>
  );
}

export default ContactForm;
